/* Placeholder JS for FullCalendar. */
/* This is a lightweight shim so the calendar can still initialize offline testing.
   For production, replace with the real FullCalendar v5.11.3 minified JS. */
window.FullCalendar = window.FullCalendar || (function(){
  function Calendar(el, opts){
    this._el = el; this._opts = opts || {}; this._events = [];
  }
  Calendar.prototype.render = function(){
    if (!this._el) return;
    var title = document.createElement('div');
    title.textContent = 'Calendar (offline placeholder)';
    title.style.color = '#888';
    this._el.appendChild(title);
  };
  Calendar.prototype.removeAllEvents = function(){ this._events = []; };
  Calendar.prototype.addEventSource = function(evts){ this._events = this._events.concat(evts||[]); };
  Object.defineProperty(Calendar.prototype, 'view', { get: function(){
    var start = new Date(); start.setDate(1);
    var end = new Date(start.getFullYear(), start.getMonth()+1, 1);
    return { activeStart: start, activeEnd: end };
  }});
  return { Calendar: Calendar };
})();
